import "./note.css"
import {useEffect, useState} from "react";
import {IsAuth} from '../../isAuth/isAuth'
import { Navigate } from 'react-router-dom';

export const Note = ({text, date}) => {
    return (
        <div className="note">
            <div className="note_info">
                <span>{text}</span>
                <span>{date}</span>
            </div>
            <div className="note_functional">
                <button className="btn_delete">Удалить</button>
            </div>
        </div>
    )
}


export const GetNotes = () => {
    if (!IsAuth()) {
        return <Navigate to='/signup'  />
    }
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [data, setData] = useState(null)
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [isLoading, setLoading] = useState(true)
    // eslint-disable-next-line react-hooks/rules-of-hooks
    useEffect(()=>{
        const fetchData=async()=>{
            await fetch('http://localhost:8000/api/notes', {'credentials': "include", 'method': 'GET'})
                .then(stats => stats.json())
                .then(data => {
                    setData(data)
                })
            setLoading(false)
        }
        fetchData()
    }, [])
    console.log(data)
    if (isLoading) return <h1>Loading...</h1>
    if (data !== null) {
        let dataToMap = data
    }

    if (data!=null) {
        return (
            <>
                <div className="notes">
                    <input placeholder="Создайте новую заметку"/>
                    {data.map(note => <Note text={note.text} date={note.date}/>)}
                </div>
            </>
        )
    }
}
